import React, { useState, useEffect } from 'react';
import { Box, Typography, LinearProgress } from '@mui/material';
import { keyframes } from '@emotion/react';

const CenteredDiv = ({ layoutMode }) => {
    const [divMessage, setDivMessage] = useState('I am perfectly centered.');
    const [windowSize, setWindowSize] = useState({
        width: window.innerWidth,
        height: window.innerHeight,
    });
    const [centerScore, setCenterScore] = useState(100);
    const [confession, setConfession] = useState('');
    const [debugMessages, setDebugMessages] = useState([]);

    const confessionPool = [
        "Sometimes I wish I were a header. So much more respect. ðŸ˜”",
        "Flexbox therapy didnâ€™t work for me.",
        "Iâ€™m scared of z-index wars. Too much drama.",
    ];

    const chaosDebugPool = [
        "Z-Index Overload! The div is lost in the stack!",
        "Negative Margins Detected! Your div is rebelling!",
        "Random Padding Explosion!",
        "The grid system has abandoned you.",
        "Parent container collapsedâ€”good luck!",
        "Margin Collapse Detectedâ€”it's every div for itself!",
        "The flex container has snapped!",
        "Absolute positioning chaos!",
        "CSS Grid is demanding better alignment terms!",
    ];

    useEffect(() => {
        const handleResize = () => {
            const newWidth = window.innerWidth;
            const newHeight = window.innerHeight;

            setWindowSize({ width: newWidth, height: newHeight });

            const centerOffset = Math.abs(newWidth / 2 - newHeight / 2);
            const scalingFactor = 200;
            const score = Math.max(0, 100 - Math.round((centerOffset / scalingFactor) * 100));

            setCenterScore(score);

            if (score === 100) {
                setDivMessage('I am perfectly centered. Bliss achieved. âœ¨');
            } else if (score > 70) {
                setDivMessage('Pretty close, but I can feel the imbalance. ðŸ§');
            } else if (score > 30) {
                setDivMessage('Centering is hard work! ðŸ˜¤');
            } else {
                setDivMessage('Help! Iâ€™m lost in the margins! ðŸ˜±');
            }
        };

        handleResize();
        window.addEventListener('resize', handleResize);

        return () => window.removeEventListener('resize', handleResize);
    }, []);

    const triggerConfession = () => {
        const randomConfession = confessionPool[Math.floor(Math.random() * confessionPool.length)];
        setConfession(randomConfession);

        // Clear confession after 5 seconds
        setTimeout(() => setConfession(''), 5000);
    };

    const triggerChaos = () => {
        const randomDebug = chaosDebugPool[Math.floor(Math.random() * chaosDebugPool.length)];

        // Generate random position and style for the debug message
        const randomStyle = {
            position: 'absolute',
            top: `${Math.random() * 80}%`, // Random vertical position
            left: `${Math.random() * 80}%`, // Random horizontal position
            color: `hsl(${Math.random() * 360}, 100%, 50%)`, // Random color
            fontSize: `${Math.random() * 1.5 + 1}rem`, // Random size between 1rem and 2.5rem
            fontWeight: 'bold',
            zIndex: 1000,
            animation: `fadeOut ${Math.random() * 2 + 1}s ease-out forwards`,
        };

        setDebugMessages((prevMessages) => [
            ...prevMessages,
            { id: Date.now(), text: randomDebug, style: randomStyle },
        ]);

        // Remove the message after 5 seconds
        setTimeout(() => {
            setDebugMessages((prevMessages) =>
                prevMessages.filter((message) => message.id !== randomDebug.id)
            );
        }, 5000);
    };

    useEffect(() => {
        if (layoutMode === 'chaotic') {
            const interval = setInterval(triggerChaos, 1500); // Chaos every 1.5 seconds
            return () => clearInterval(interval);
        }
    }, [layoutMode]);

    const generateDynamicShake = () => {
        const intensity = Math.max(1, (100 - centerScore) / 10);

        return keyframes`
            0% { transform: translateX(0); }
            25% { transform: translateX(-${intensity}px); }
            50% { transform: translateX(${intensity}px); }
            75% { transform: translateX(-${intensity}px); }
            100% { transform: translateX(0); }
        `;
    };

    return (
        <Box
            className="chaotic-div"
            onMouseEnter={triggerConfession}
            sx={{
                border: '1px solid #ccc',
                borderRadius: '10px',
                padding: '1rem',
                textAlign: 'center',
                marginBottom: '1.5rem',
                position: 'relative',
                width: layoutMode === 'grid' ? '30%' : '50%',
                margin: layoutMode === 'chaotic' ? 'random' : '0 auto',
                backgroundColor: layoutMode === 'chaotic' ? '#fff3cd' : '#f5f5f5',
                animation: centerScore === 100
                    ? 'none'
                    : `${generateDynamicShake()} ${Math.max(0.2, centerScore / 100)}s ease-in-out infinite`,
            }}
        >
            <Typography variant="body1" gutterBottom>
                {divMessage}
            </Typography>
            <Typography variant="body2" gutterBottom>
                Window Size: {windowSize.width}px x {windowSize.height}px
            </Typography>
            <LinearProgress
                variant="determinate"
                value={centerScore}
                sx={{
                    height: 10,
                    borderRadius: 5,
                    backgroundColor: '#e0e0e0',
                    '& .MuiLinearProgress-bar': {
                        backgroundColor: centerScore === 100 ? '#4caf50' : '#ff5722',
                    },
                }}
            />
            <Typography variant="body2" sx={{ marginTop: '0.5rem' }}>
                Centeredness: {centerScore}%
            </Typography>
            {confession && (
                <Typography
                    variant="body2"
                    sx={{
                        marginTop: '1rem',
                        fontStyle: 'italic',
                        color: '#757575',
                    }}
                >
                    {confession}
                </Typography>
            )}
            {debugMessages.map((message) => (
                <Typography
                    key={message.id}
                    variant="body2"
                    sx={message.style}
                >
                    {message.text}
                </Typography>
            ))}
        </Box>
    );
};

export default CenteredDiv;
