// Chaos.js
const triggerExtremeChaos = (element) => {
    if (!element) return;

    const randomRotation = Math.floor(Math.random() * 360); // 0-359 degrees
    const randomScale = 0.5 + Math.random() * 1.5; // Scale between 0.5 and 2.0
    const randomColor = `hsl(${Math.floor(Math.random() * 360)}, 100%, 80%)`; // Random pastel color

    element.style.transform = `rotate(${randomRotation}deg) scale(${randomScale})`;
    element.style.backgroundColor = randomColor;

    // Reset after a few seconds for sanity
    setTimeout(() => {
        element.style.transform = 'none';
        element.style.backgroundColor = '';
    }, 5000);
};

const triggerDramaAlert = (setDramaAlert, message) => {
    setDramaAlert(message);
    setTimeout(() => setDramaAlert(''), 3000);
};

export { triggerExtremeChaos, triggerDramaAlert };
