import React, { useState, useEffect } from 'react';
import { Box, Typography, LinearProgress, Modal, Button } from '@mui/material';
import { keyframes } from '@emotion/react';
import { triggerExtremeChaos, triggerDramaAlert } from './Chaos';

const CenteredDiv = ({ layoutMode }) => {
    const [divMessage, setDivMessage] = useState('I am perfectly centered.');
    const [windowSize, setWindowSize] = useState({
        width: window.innerWidth,
        height: window.innerHeight,
    });
    const [centerScore, setCenterScore] = useState(100);
    const [confession, setConfession] = useState('');
    const [debugMessage, setDebugMessage] = useState('');
    const [dramaAlert, setDramaAlert] = useState('');
    const [showGifModal, setShowGifModal] = useState(false);
    const [selectedGif, setSelectedGif] = useState('');

    const confessionPool = [
        "I deserve a dedicated stylesheet, darling. ðŸ•¶ï¸",
        "Is this my best angle? Center me again, sweetie. ðŸ’‹",
        "Grid systems are my runway.",
        "I sparkle brighter with more padding. ðŸ’–",
        "Donâ€™t box me inâ€”Iâ€™m a free spirit, honey! ðŸŒŸ",
    ];

    const gifPool = [
        'https://media.giphy.com/media/3o6Zt481isNVuQI1l6/giphy.gif', // Disapproving cat
        'https://media.giphy.com/media/l0MYt5jPR6QX5pnqM/giphy.gif', // Sassy finger wag
        'https://media.giphy.com/media/3ohhwn7ZELkAByPIk4/giphy.gif', // Eye roll
        'https://media.giphy.com/media/xT9IgIc0lryrxvqVGM/giphy.gif', // Dramatic sigh
    ];

    // Resize handler
    useEffect(() => {
        const handleResize = () => {
            const newWidth = window.innerWidth;
            const newHeight = window.innerHeight;

            setWindowSize({ width: newWidth, height: newHeight });

            const centerOffset = Math.abs(newWidth / 2 - newHeight / 2);
            const scalingFactor = 200;
            const score = Math.max(0, 100 - Math.round((centerOffset / scalingFactor) * 100));

            setCenterScore(score);

            if (score === 100) {
                setDivMessage('I am perfectly centered. Bliss achieved. âœ¨');
            } else if (score > 70) {
                setDivMessage('Pretty close, but I can feel the imbalance. ðŸ§');
            } else if (score > 30) {
                setDivMessage('Centering is hard work! ðŸ˜¤');
            } else if (score > 10) {
                setDivMessage('Help! Iâ€™m lost in the margins! ðŸ˜±');
            } else {
                setDivMessage('');
                // Trigger sassy mode
                triggerDramaAlert(setDramaAlert, "Sassy Mode Activated! ðŸ’…");
                triggerSassyText();
                triggerSassyGif();
            }
        };

        handleResize();
        window.addEventListener('resize', handleResize);

        return () => window.removeEventListener('resize', handleResize);
    }, []);

    const triggerConfession = () => {
        const randomConfession = confessionPool[Math.floor(Math.random() * confessionPool.length)];
        setConfession(randomConfession);

        // Clear confession after 5 seconds
        setTimeout(() => setConfession(''), 5000);
    };

    const triggerSassyText = () => {
        const sassyText = document.createElement('div');
        sassyText.textContent = 'Really? This is unacceptable. ðŸ™„';
        Object.assign(sassyText.style, {
            fontFamily: 'Comic Sans MS, sans-serif',
            fontSize: '2rem',
            color: '#ff0000',
            position: 'fixed',
            top: `${Math.random() * 80}%`,
            left: `${Math.random() * 80}%`,
            zIndex: 9999,
            animation: 'fadeOut 5s ease-in-out',
        });

        document.body.appendChild(sassyText);

        // Remove text after animation
        setTimeout(() => sassyText.remove(), 5000);
    };

    const triggerSassyGif = () => {
        const randomGif = gifPool[Math.floor(Math.random() * gifPool.length)];
        setSelectedGif(randomGif);
        setShowGifModal(true);
    };

    const closeGifModal = () => setShowGifModal(false);

    const generateDynamicShake = () => {
        const intensity = Math.max(1, (100 - centerScore) / 10);

        return keyframes`
            0% { transform: translateX(0); }
            25% { transform: translateX(-${intensity}px); }
            50% { transform: translateX(${intensity}px); }
            75% { transform: translateX(-${intensity}px); }
            100% { transform: translateX(0); }
        `;
    };

    return (
        <Box
            className="chaotic-div"
            onMouseEnter={triggerConfession}
            sx={{
                borderRadius: '10px',
                padding: '1rem',
                textAlign: 'center',
                marginBottom: '1.5rem',
                position: 'relative',
                width: layoutMode === 'grid' ? '30%' : '50%',
                margin: layoutMode === 'chaotic' ? 'random' : '0 auto',
                backgroundColor: layoutMode === 'chaotic' ? '#fff3cd' : '#f5f5f5',
                animation: centerScore === 100
                    ? 'none'
                    : `${generateDynamicShake()} ${Math.max(0.2, centerScore / 100)}s ease-in-out infinite`,
            }}
        >
            <Typography variant="body1" gutterBottom>
                {divMessage}
            </Typography>
            <Typography variant="body2" gutterBottom>
                Window Size: {windowSize.width}px x {windowSize.height}px
            </Typography>
            <LinearProgress
                variant="determinate"
                value={centerScore}
                sx={{
                    height: 10,
                    borderRadius: 5,
                    backgroundColor: '#e0e0e0',
                    '& .MuiLinearProgress-bar': {
                        backgroundColor: centerScore === 100 ? '#4caf50' : '#ff5722',
                    },
                }}
            />
            <Typography variant="body2" sx={{ marginTop: '0.5rem' }}>
                Centeredness: {centerScore}%
            </Typography>
            {confession && (
                <Typography
                    variant="body2"
                    sx={{
                        marginTop: '1rem',
                        fontStyle: 'italic',
                        color: '#757575',
                    }}
                >
                    {confession}
                </Typography>
            )}
            {dramaAlert && (
                <Typography
                    variant="body2"
                    sx={{
                        marginTop: '1rem',
                        fontWeight: 'bold',
                        color: 'purple',
                    }}
                >
                    {dramaAlert}
                </Typography>
            )}
            <Modal open={showGifModal} onClose={closeGifModal}>
                <Box
                    sx={{
                        position: 'absolute',
                        top: '50%',
                        left: '50%',
                        transform: 'translate(-50%, -50%)',
                        bgcolor: 'background.paper',
                        boxShadow: 24,
                        p: 2,
                        textAlign: 'center',
                    }}
                >
                    <img src={selectedGif} alt="Sassy GIF" style={{ maxWidth: '100%' }} />
                    <Button
                        variant="contained"
                        color="secondary"
                        sx={{ mt: 2 }}
                        onClick={closeGifModal}
                    >
                        Close
                    </Button>
                </Box>
            </Modal>
        </Box>
    );
};

export default CenteredDiv;
