import React, { useState, useEffect } from 'react';
import { Box, Typography, LinearProgress } from '@mui/material';
import { keyframes } from '@emotion/react';

const CenteredDiv = () => {
    const [divMessage, setDivMessage] = useState('I am perfectly centered.');
    const [windowSize, setWindowSize] = useState({
        width: window.innerWidth,
        height: window.innerHeight,
    });
    const [centerScore, setCenterScore] = useState(100);
    const [confession, setConfession] = useState('');
    const [isChaoticMode, setIsChaoticMode] = useState(false);
    const [debugMessage, setDebugMessage] = useState('');

    // List of funny confessions
    const confessionPool = [
        "Sometimes I wish I were a header. So much more respect. 😔",
        "I secretly love being absolute... but don’t tell the flexbox gang.",
        "I think the padding gets more attention than I do. 😢",
        "I pretend to hate margins, but I secretly appreciate the space.",
        "Flexbox therapy didn’t work for me.",
        "I’m scared of z-index wars. Too much drama.",
        "I hate floats. There, I said it. 😤",
        "Does anyone actually validate their CSS anymore?",
        "I have nightmares about being deprecated.",
        "My dream is to be the center of attention—literally.",
        "I’m scared of being uncentered. It’s my worst nightmare.",
    ];

    // Function to handle window resize
    useEffect(() => {
        const handleResize = () => {
            const newWidth = window.innerWidth;
            const newHeight = window.innerHeight;

            setWindowSize({ width: newWidth, height: newHeight });

            const centerOffset = Math.abs(newWidth / 2 - newHeight / 2);
            const scalingFactor = 200;
            const score = Math.max(0, 100 - Math.round((centerOffset / scalingFactor) * 100));

            setCenterScore(score);

            if (score === 100) {
                setDivMessage('I am perfectly centered. Bliss achieved. ✨');
            } else if (score > 70) {
                setDivMessage('Pretty close, but I can feel the imbalance. 🧐');
            } else if (score > 30) {
                setDivMessage('Centering is hard work! 😤');
            } else {
                setDivMessage('Help! I’m lost in the margins! 😱');
            }
        };

        handleResize();
        window.addEventListener('resize', handleResize);

        return () => window.removeEventListener('resize', handleResize);
    }, []);

    // Function to trigger a random confession
    const triggerConfession = () => {
        const randomConfession = confessionPool[Math.floor(Math.random() * confessionPool.length)];
        setConfession(randomConfession);

        // Clear confession after 3 seconds
        setTimeout(() => setConfession(''), 5000);
    };

    // Function to trigger chaos
    const triggerChaos = () => {
        const chaosTypes = [
            "Z-Index Overload! The div is lost in the stack!",
            "Negative Margins Detected! Your div is rebelling!",
            "Random Padding Explosion!",
            "The grid system has abandoned you.",
            "Parent container collapsed—good luck!",
            "Inline styles are taking over!"
        ];

        const randomChaos = chaosTypes[Math.floor(Math.random() * chaosTypes.length)];
        setDebugMessage(randomChaos);

        // Example of CSS disruptions
        setTimeout(() => {
            const randomMargin = Math.floor(Math.random() * 50 - 25); // Range: -25px to +25px
            const randomZIndex = Math.floor(Math.random() * 10); // Random z-index from 0 to 10
            const randomPadding = Math.floor(Math.random() * 50); // Padding 0-50px

            document.querySelector('.chaotic-div').style.margin = `${randomMargin}px`;
            document.querySelector('.chaotic-div').style.zIndex = randomZIndex;
            document.querySelector('.chaotic-div').style.padding = `${randomPadding}px`;
        }, 500);

        // Reset chaos after a brief period
        setTimeout(() => {
            setDebugMessage('');
        }, 5000);
    };

    // Automatically trigger chaos in chaotic mode
    useEffect(() => {
        if (isChaoticMode) {
            const interval = setInterval(triggerChaos, 3000);
            return () => clearInterval(interval);
        }
    }, [isChaoticMode]);

    const generateDynamicShake = () => {
        const intensity = Math.max(1, (100 - centerScore) / 10); // Intensity increases as score decreases

        return keyframes`
            0% { transform: translateX(0); }
            25% { transform: translateX(-${intensity}px); }
            50% { transform: translateX(${intensity}px); }
            75% { transform: translateX(-${intensity}px); }
            100% { transform: translateX(0); }
        `;
    };

    return (
        <Box
            className="chaotic-div"
            onMouseEnter={triggerConfession} // Trigger confession on hover
            sx={{
                border: '1px solid #ccc',
                borderRadius: '10px',
                padding: '1rem',
                textAlign: 'center',
                marginBottom: '1.5rem',
                position: 'relative',
                width: '50%',
                margin: '0 auto',
                backgroundColor: isChaoticMode ? '#fff3cd' : '#f5f5f5', // Highlight during chaos
                animation: centerScore === 100
                    ? 'none'
                    : `${generateDynamicShake()} ${Math.max(0.2, centerScore / 100)}s ease-in-out infinite`,
            }}
        >
            <Typography variant="body1" gutterBottom>
                {divMessage}
            </Typography>
            <Typography variant="body2" gutterBottom>
                Window Size: {windowSize.width}px x {windowSize.height}px
            </Typography>
            <LinearProgress
                variant="determinate"
                value={centerScore}
                sx={{
                    height: 10,
                    borderRadius: 5,
                    backgroundColor: '#e0e0e0',
                    '& .MuiLinearProgress-bar': {
                        backgroundColor: centerScore === 100 ? '#4caf50' : '#ff5722',
                    },
                }}
            />
            <Typography variant="body2" sx={{ marginTop: '0.5rem' }}>
                Centeredness: {centerScore}%
            </Typography>
            {confession && (
                <Typography
                    variant="body2"
                    sx={{
                        marginTop: '1rem',
                        fontStyle: 'italic',
                        color: '#757575',
                    }}
                >
                    {confession}
                </Typography>
            )}
            {debugMessage && (
                <Typography
                    variant="body2"
                    sx={{
                        marginTop: '1rem',
                        fontStyle: 'italic',
                        color: 'red',
                    }}
                >
                    Debug: {debugMessage}
                </Typography>
            )}
            <Box sx={{ textAlign: 'center', marginTop: '1rem' }}>
                <button onClick={() => setIsChaoticMode(!isChaoticMode)}>
                    {isChaoticMode ? "Disable Chaotic Mode" : "Enable Chaotic Mode"}
                </button>
            </Box>
        </Box>
    );
};

export default CenteredDiv;
