import React, { useState, useEffect } from 'react';
import { Box, Typography, LinearProgress } from '@mui/material';
import { keyframes } from '@emotion/react';

const CenteredDiv = () => {
    const [divMessage, setDivMessage] = useState('I am perfectly centered.');
    const [windowSize, setWindowSize] = useState({
        width: window.innerWidth,
        height: window.innerHeight,
    });
    const [centerScore, setCenterScore] = useState(100);
    const [confession, setConfession] = useState('');

    // List of funny confessions
    const confessionPool = [
        "Sometimes I wish I were a header. So much more respect. üòî",
        "I secretly love being absolute... but don‚Äôt tell the flexbox gang.",
        "I think the padding gets more attention than I do. üò¢",
        "I pretend to hate margins, but I secretly appreciate the space.",
        "I tried to align-left once. Never again.",
        "I don‚Äôt feel included in grid layouts. üò≠",
        "I can‚Äôt stand sticky positioning‚Äîit‚Äôs too clingy. üòí",
        "I live in fear of being hidden with `display: none;`.",
        "Sometimes I think inline-blocks are cooler than me.",
        "Float-right divs make me feel inadequate.",
        "Flexbox therapy didn‚Äôt work for me.",
        "I‚Äôm not jealous of the grid system, okay?!",
        "Position: fixed? I need freedom, not anchors.",
        "I‚Äôm scared of z-index wars. Too much drama.",
        "I feel empty without children. üë∂",
        "I just want to overflow with joy. Instead, it‚Äôs scrollbars.",
        "I once went outside the viewport and never recovered.",
        "No one understands the struggles of negative margins.",
        "Sometimes I wish someone would center me manually. üõ†Ô∏è",
        "I hate floats. There, I said it. üò§",
        "Why do some devs insist on using `!important`? It‚Äôs rude.",
        "I tried to collapse my margins once‚Äîit wasn‚Äôt pretty.",
        "Grid layouts make me feel outdated.",
        "I think I‚Äôm allergic to inline styles.",
        "I feel stuck when I‚Äôm absolutely positioned.",
        "I hate being overflow: hidden; it‚Äôs like I don‚Äôt exist.",
        "Z-index hierarchies are so elitist. üôÑ",
        "I don‚Äôt need a border radius to feel whole. üü¶",
        "Padding is my comfort zone. Don‚Äôt judge me.",
        "Display: block. It‚Äôs a lifestyle.",
        "I think the pseudo-elements are cooler than me. üòî",
        "Do you think I could make it in the SVG world?",
        "I get anxious when people resize their windows.",
        "I‚Äôm just a div trying to make it in a grid world.",
        "I once had perfect alignment... until the media query hit.",
        "Being centered is exhausting. üòÆ‚Äçüí®",
        "I think I peaked in IE6. It‚Äôs all been downhill since.",
        "Does anyone actually validate their CSS anymore?",
        "I have nightmares about being deprecated.",
        "I hate being nested. Let me live on my own!",
        "Do I even matter without content?",
        "I tried to be part of a form once. It didn‚Äôt go well.",
        "JavaScript thinks it‚Äôs so smart, but I‚Äôm the real MVP.",
        "I‚Äôm not a fan of min-width. It‚Äôs too restrictive.",
        "When the flex container snaps, I break inside.",
        "I wish I could float effortlessly like a balloon. üéà",
        "Sometimes I wonder if I‚Äôm just a placeholder in life. ü§î",
        "Whitespace makes me feel incomplete.",
        "I once got replaced by a span. It still hurts.",
        "Do I even belong in the DOM?",
        "I feel invisible when my opacity is less than 1. üò∂",
        "The box model makes me feel boxed in. üì¶",
        "I think I‚Äôd thrive as a React component.",
        "I wish I were a button. People actually click on those.",
        "Does anyone even care about semantics anymore?",
        "Sometimes I feel like I‚Äôm padding everyone else‚Äôs success.",
        "Why does everything have to be responsive? I need stability.",
        "I tried to flex, but I don‚Äôt feel justified.",
        "I once got stuck in a sticky footer. It was traumatic.",
        "CSS variables make me feel irrelevant.",
        "I want to overflow with content, but I‚Äôm scared to break the layout.",
        "My id gets ignored, but everyone loves the classes.",
        "Being replaced by a `<main>` really stung. üíî",
        "Is clearfix even still a thing? I miss the old days.",
        "I feel like no one understands me without a CSS reset.",
        "I think I‚Äôm addicted to transition effects.",
        "I hate being display: none. It‚Äôs like I don‚Äôt exist.",
        "My max-width feels so limiting. Let me expand!",
        "Do you think SVG elements look down on me?",
        "I think my borders are too thick... I mean, bold.",
        "I want to be dynamic, but static positioning holds me back.",
        "I envy the simplicity of `text-align: center`.",
        "I don‚Äôt trust media queries. They change everything about me.",
        "My dream is to be the center of attention‚Äîliterally.",
        "I once overflowed the viewport. It was liberating. ü§©",
        "I get nervous when someone uses a CSS preprocessor.",
        "Flexbox containers feel like cliques. I don‚Äôt fit in.",
        "I‚Äôm afraid of heights... or anything above z-index: 10.",
        "I tried to be relative once. It was complicated.",
        "Sometimes I wish I could be a shadow DOM element.",
        "When devs use `important!`, I feel... less important.",
        "I get tired of being the fallback element. Can‚Äôt I shine?",
        "I feel incomplete without a pseudo-element buddy.",
        "Margins make me feel distant from others.",
        "I once got styled by a CSS animation‚Äîit changed me.",
        "CSS-in-JS makes me feel like I‚Äôm losing my identity.",
        "I want to be inline-flex, but I‚Äôm scared of being flexible.",
        "I wish someone would validate my existence... and my CSS.",
        "Do you think HTML5 really improved my life?",
        "I‚Äôm scared of being uncentered. It‚Äôs my worst nightmare.",
        "Sometimes I want to take a break and just be a span."
    ];


    // Function to handle window resize
    useEffect(() => {
        const handleResize = () => {
            const newWidth = window.innerWidth;
            const newHeight = window.innerHeight;

            setWindowSize({ width: newWidth, height: newHeight });

            const centerOffset = Math.abs(newWidth / 2 - newHeight / 2);
            const scalingFactor = 200;
            const score = Math.max(0, 100 - Math.round((centerOffset / scalingFactor) * 100));

            setCenterScore(score);

            if (score === 100) {
                setDivMessage('I am perfectly centered. Bliss achieved. ‚ú®');
            } else if (score > 70) {
                setDivMessage('Pretty close, but I can feel the imbalance. üßê');
            } else if (score > 30) {
                setDivMessage('Centering is hard work! üò§');
            } else {
                setDivMessage('Help! I‚Äôm lost in the margins! üò±');
            }
        };

        handleResize();
        window.addEventListener('resize', handleResize);

        return () => window.removeEventListener('resize', handleResize);
    }, []);

    // Function to trigger a random confession
    const triggerConfession = () => {
        const randomConfession = confessionPool[Math.floor(Math.random() * confessionPool.length)];
        setConfession(randomConfession);

        // Clear confession after 3 seconds
        setTimeout(() => setConfession(''), 3000);
    };

    const generateDynamicShake = () => {
        const intensity = Math.max(1, (100 - centerScore) / 10); // Intensity increases as score decreases

        return keyframes`
            0% { transform: translateX(0); }
            25% { transform: translateX(-${intensity}px); }
            50% { transform: translateX(${intensity}px); }
            75% { transform: translateX(-${intensity}px); }
            100% { transform: translateX(0); }
        `;
    };

    return (
        <Box
            onMouseEnter={triggerConfession} // Trigger confession on hover
            sx={{
                border: '1px solid #ccc',
                borderRadius: '10px',
                padding: '1rem',
                textAlign: 'center',
                marginBottom: '1.5rem',
                position: 'relative',
                width: '50%',
                margin: '0 auto',
                backgroundColor: '#f5f5f5',
                animation: centerScore === 100
                    ? 'none'
                    : `${generateDynamicShake()} ${Math.max(0.2, centerScore / 100)}s ease-in-out infinite`,
            }}
        >
            <Typography variant="body1" gutterBottom>
                {divMessage}
            </Typography>
            <Typography variant="body2" gutterBottom>
                Window Size: {windowSize.width}px x {windowSize.height}px
            </Typography>
            <LinearProgress
                variant="determinate"
                value={centerScore}
                sx={{
                    height: 10,
                    borderRadius: 5,
                    backgroundColor: '#e0e0e0',
                    '& .MuiLinearProgress-bar': {
                        backgroundColor: centerScore === 100 ? '#4caf50' : '#ff5722',
                    },
                }}
            />
            <Typography variant="body2" sx={{ marginTop: '0.5rem' }}>
                Centeredness: {centerScore}%
            </Typography>
            {confession && (
                <Typography
                    variant="body2"
                    sx={{
                        marginTop: '1rem',
                        fontStyle: 'italic',
                        color: '#757575',
                    }}
                >
                    {confession}
                </Typography>
            )}
        </Box>
    );
};

export default CenteredDiv;
