import React, { useState } from 'react';
import { Box, Button, keyframes } from '@mui/material';
import CenteredDiv from './CenteredDiv';

const LayoutMode = () => {
    const [currentLayout, setCurrentLayout] = useState('default');

    // Define different layout styles
    const layouts = {
        default: {
            display: 'flex',
            justifyContent: 'center',
            alignItems: 'center',
            height: '300px',
        },
        grid: {
            display: 'grid',
            gridTemplateColumns: 'repeat(3, 1fr)',
            gridGap: '10px',
            height: '300px',
        },
        chaotic: {
            position: 'relative',
            height: '300px',
        },
    };

    const generateRandomKeyframes = () => {
        return keyframes`
            0% {
                transform: translate(0, 0);
            }
            25% {
                transform: translate(${Math.random() * 100 - 50}px, ${Math.random() * 100 - 50}px);
            }
            50% {
                transform: translate(${Math.random() * 100 - 50}px, ${Math.random() * 100 - 50}px);
            }
            75% {
                transform: translate(${Math.random() * 100 - 50}px, ${Math.random() * 100 - 50}px);
            }
            100% {
                transform: translate(0, 0);
            }
        `;
    };

    const generateChaos = () => {
        const chaosDivs = Array.from({ length: 5 }, (_, i) => {
            const randomKeyframes = generateRandomKeyframes();
            const randomDuration = `${Math.random() * 3 + 2}s`; // Random duration between 2s and 5s
            const randomDelay = `${Math.random()}s`; // Random delay between 0s and 1s

            return (
                <Box
                    key={i}
                    sx={{
                        position: 'absolute',
                        top: `${Math.random() * 80}%`,
                        left: `${Math.random() * 80}%`,
                        width: '50px',
                        height: '50px',
                        backgroundColor: 'rgba(255,0,0,0.5)',
                        animation: `${randomKeyframes} ${randomDuration} ease-in-out ${randomDelay} infinite`,
                    }}
                />
            );
        });
        return chaosDivs;
    };

    return (
        <Box>
            <Box sx={{ display: 'flex', justifyContent: 'center', gap: '1rem', marginBottom: '1rem' }}>
                <Button variant="outlined" onClick={() => setCurrentLayout('default')}>
                    Default Layout
                </Button>
                <Button variant="outlined" onClick={() => setCurrentLayout('grid')}>
                    Grid Layout
                </Button>
                <Button variant="outlined" onClick={() => setCurrentLayout('chaotic')}>
                    Chaotic Layout
                </Button>
            </Box>
            <Box sx={{ border: '1px solid #ccc', borderRadius: '8px', ...layouts[currentLayout] }}>
                {currentLayout === 'chaotic' && generateChaos()}
                <CenteredDiv />
            </Box>
        </Box>
    );
};

export default LayoutMode;
