import React, { useState, useEffect, useRef } from 'react';
import {
    Container,
    TextField,
    Button,
    Typography,
    Box,
} from '@mui/material';

const MainContent = () => {
    const [input, setInput] = useState('');
    const [error, setError] = useState('');
    const [divMessage, setDivMessage] = useState('I am perfectly centered.');
    const [windowSize, setWindowSize] = useState({
        width: window.innerWidth,
        height: window.innerHeight,
    });
    const [chatHistory, setChatHistory] = useState([]);
    const chatBoxRef = useRef(null);

    const handleChatSubmit = async (e) => {
        e.preventDefault();
        if (input.trim() === '') {
            setError('Message cannot be empty.');
            return;
        }

        const userMessage = { type: 'user', text: input };
        setChatHistory((prevHistory) => [...prevHistory, userMessage]);

        try {
            // Call the backend for AI response
            const response = await fetch('http://localhost:5001/chat', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ message: input }),
            });

            const data = await response.json();

            const botMessage = { type: 'bot', text: data.reply };
            setChatHistory((prevHistory) => [...prevHistory, botMessage]);
        } catch (error) {
            console.error('Error fetching AI response:', error);
            const errorMessage = { type: 'bot', text: 'Oops! I ran into an error.' };
            setChatHistory((prevHistory) => [...prevHistory, errorMessage]);
        }

        setInput('');
    };

    useEffect(() => {
        const handleResize = () => {
            const newWidth = window.innerWidth;
            const newHeight = window.innerHeight;

            setWindowSize({ width: newWidth, height: newHeight });

            if (newWidth < 600) {
                setDivMessage('Why is it so cramped in here? ðŸ¥´');
            } else if (newWidth > 1200) {
                setDivMessage('I feel so exposed! ðŸ˜±');
            } else {
                setDivMessage('I am perfectly centered.');
            }
        };

        window.addEventListener('resize', handleResize);
        return () => window.removeEventListener('resize', handleResize);
    }, []);

    // Scroll chat box to the bottom when chat history changes
    useEffect(() => {
        if (chatBoxRef.current) {
            chatBoxRef.current.scrollTop = chatBoxRef.current.scrollHeight;
        }
    }, [chatHistory]);

    return (
        <Container maxWidth="sm" style={{ padding: '2rem', textAlign: 'center' }}>
            <Typography variant="h4" gutterBottom>
                Centered Div Therapy
            </Typography>
            <Typography variant="body1" gutterBottom style={{ marginBottom: '1rem', fontStyle: 'italic' }}>
                {divMessage}
            </Typography>
            <Typography variant="body2" style={{ marginBottom: '1rem' }}>
                {`Window Size: ${windowSize.width}px x ${windowSize.height}px`}
            </Typography>

            {/* Chat Section */}
            <Box
                ref={chatBoxRef}
                sx={{
                    border: '1px solid #ccc',
                    borderRadius: '10px',
                    padding: '1rem',
                    marginBottom: '1.5rem',
                    height: '200px',
                    overflowY: 'auto',
                }}
            >
                {chatHistory.map((chat, index) => (
                    <Typography
                        key={index}
                        variant="body1"
                        align={chat.type === 'user' ? 'right' : 'left'}
                        style={{
                            color: chat.type === 'user' ? 'blue' : 'green',
                            marginBottom: '0.5rem',
                        }}
                    >
                        {chat.text}
                    </Typography>
                ))}
            </Box>
            <form onSubmit={handleChatSubmit}>
                <TextField
                    fullWidth
                    variant="outlined"
                    label="Type your message"
                    value={input}
                    onChange={(e) => setInput(e.target.value)}
                    error={!!error}
                    helperText={error}
                    style={{ marginBottom: '1rem' }}
                />
                <Button
                    variant="contained"
                    color="primary"
                    type="submit"
                    style={{ width: '100%' }}
                >
                    Send
                </Button>
            </form>
        </Container>
    );
};

export default MainContent;
