import React, { useState } from 'react';
import axios from 'axios';

const MainContent = () => {
    const [input, setInput] = useState('');
    const [responseMessage, setResponseMessage] = useState('');
    const [error, setError] = useState('');

    const sendData = async () => {
        try {
            const response = await axios.post('http://localhost:5000/data', { input });
            setResponseMessage(response.data.message);
            setError('');
        } catch (err) {
            console.error('Error sending data:', err);
            setResponseMessage('');
            setError('Could not send data to the backend.');
        }
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        if (input.trim() === '') {
            setError('Input cannot be empty.');
            return;
        }
        if (input.length > 50) {
            setError('Input must be 50 characters or less.');
            return;
        }
        sendData();
        setInput('');
    };

    return (
        <main style={{ padding: '2rem', textAlign: 'center' }}>
            <div style={{ margin: 'auto', padding: '1rem', width: '300px', backgroundColor: '#61dafb', borderRadius: '10px' }}>
                <form onSubmit={handleSubmit}>
                    <input
                        type="text"
                        value={input}
                        onChange={(e) => setInput(e.target.value)}
                        placeholder="Send data to backend"
                        style={{ padding: '0.5rem', width: '80%', marginBottom: '1rem' }}
                    />
                    <button type="submit" style={{ padding: '0.5rem 1rem' }}>
                        Submit
                    </button>
                </form>
                {error && <p style={{ color: 'red' }}>{error}</p>}
                {responseMessage && <p>{responseMessage}</p>}
            </div>
        </main>
    );
};

export default MainContent;
