import React, { useState, useEffect } from 'react';
import { Box, Typography, LinearProgress } from '@mui/material';
import { keyframes } from '@emotion/react';

const CenteredDiv = ({ layoutMode }) => {
    const [divMessage, setDivMessage] = useState('I am perfectly centered.');
    const [windowSize, setWindowSize] = useState({
        width: window.innerWidth,
        height: window.innerHeight,
    });
    const [centerScore, setCenterScore] = useState(100);
    const [debugMessages, setDebugMessages] = useState([]);
    const [containerPosition, setContainerPosition] = useState({ top: '40%', left: '40%' });

    const confessionPool = [
        "Sometimes I wish I were a header. So much more respect. 😔",
        "Flexbox therapy didn’t work for me.",
        "I’m scared of z-index wars. Too much drama.",
    ];

    useEffect(() => {
        const handleResize = () => {
            const newWidth = window.innerWidth;
            const newHeight = window.innerHeight;

            setWindowSize({ width: newWidth, height: newHeight });

            const centerOffset = Math.abs(newWidth / 2 - newHeight / 2);
            const scalingFactor = 200;
            const score = Math.max(0, 100 - Math.round((centerOffset / scalingFactor) * 100));

            setCenterScore(score);

            if (score === 100) {
                setDivMessage('I am perfectly centered. Bliss achieved. ✨');
            } else if (score > 70) {
                setDivMessage('Pretty close, but I can feel the imbalance. 🧐');
            } else if (score > 30) {
                setDivMessage('Centering is hard work! 😤');
            } else {
                setDivMessage('Help! I’m lost in the margins! 😱');
            }
        };

        handleResize();
        window.addEventListener('resize', handleResize);

        return () => window.removeEventListener('resize', handleResize);
    }, []);

    const startContainerAnimation = () => {
        const moveContainer = () => {
            setContainerPosition({
                top: `${Math.random() * 80}%`,
                left: `${Math.random() * 80}%`,
            });
        };

        if (layoutMode === 'chaotic') {
            const interval = setInterval(moveContainer, 2000); // Move every 2 seconds
            return () => clearInterval(interval);
        }
    };

    useEffect(() => {
        const stopAnimation = startContainerAnimation();
        return stopAnimation;
    }, [layoutMode]);

    const generateBouncingSquares = () => {
        const squareStyles = [];
        for (let i = 0; i < 5; i++) {
            squareStyles.push({
                top: `${Math.random() * 90}%`,
                left: `${Math.random() * 90}%`,
                animation: `${bounceAnimation()} ${Math.random() * 2 + 1}s linear infinite`,
            });
        }
        setDebugMessages(squareStyles);
    };

    useEffect(() => {
        if (layoutMode === 'chaotic') {
            generateBouncingSquares();
        }
    }, [layoutMode]);

    const bounceAnimation = () => keyframes`
        0%, 100% { transform: translate(0, 0); }
        25% { transform: translate(20px, -20px); }
        50% { transform: translate(-20px, 20px); }
        75% { transform: translate(-20px, -20px); }
    `;

    return (
        <Box
            sx={{
                border: '2px solid red',
                borderRadius: '10px',
                padding: '1rem',
                textAlign: 'center',
                position: 'absolute',
                backgroundColor: layoutMode === 'chaotic' ? '#ffeb3b' : '#f5f5f5',
                top: containerPosition.top,
                left: containerPosition.left,
                width: '300px',
                height: '300px',
            }}
        >
            <Typography variant="body1" gutterBottom>
                {divMessage}
            </Typography>
            <Typography variant="body2" gutterBottom>
                Window Size: {windowSize.width}px x {windowSize.height}px
            </Typography>
            <LinearProgress
                variant="determinate"
                value={centerScore}
                sx={{
                    height: 10,
                    borderRadius: 5,
                    backgroundColor: '#e0e0e0',
                    '& .MuiLinearProgress-bar': {
                        backgroundColor: centerScore === 100 ? '#4caf50' : '#ff5722',
                    },
                }}
            />
            {debugMessages.map((style, index) => (
                <Box
                    key={index}
                    sx={{
                        position: 'absolute',
                        width: '20px',
                        height: '20px',
                        backgroundColor: '#d32f2f',
                        borderRadius: '50%',
                        ...style,
                    }}
                />
            ))}
        </Box>
    );
};

export default CenteredDiv;
