import React, { useState, useEffect } from 'react';
import { Container, Typography, Box } from '@mui/material';

const MainContent = () => {
    const [divMessage, setDivMessage] = useState('I am perfectly centered.');
    const [windowSize, setWindowSize] = useState({
        width: window.innerWidth,
        height: window.innerHeight,
    });

    useEffect(() => {
        const handleResize = () => {
            const newWidth = window.innerWidth;
            const newHeight = window.innerHeight;

            setWindowSize({ width: newWidth, height: newHeight });

            if (newWidth < 600) {
                setDivMessage('Why is it so cramped in here? ðŸ¥´');
            } else if (newWidth > 1200) {
                setDivMessage('I feel so exposed! ðŸ˜±');
            } else {
                setDivMessage('I am perfectly centered.');
            }
        };

        window.addEventListener('resize', handleResize);
        return () => window.removeEventListener('resize', handleResize);
    }, []);

    return (
        <Container maxWidth="sm" style={{ padding: '2rem', textAlign: 'center' }}>
            <Typography variant="h4" gutterBottom>
                Centered Div Therapy
            </Typography>
            <Typography variant="body1" gutterBottom style={{ marginBottom: '1rem', fontStyle: 'italic' }}>
                {divMessage}
            </Typography>
            <Typography variant="body2" style={{ marginBottom: '1rem' }}>
                {`Window Size: ${windowSize.width}px x ${windowSize.height}px`}
            </Typography>

            {/* Placeholder for chat section, removed for now */}
            <Box
                sx={{
                    border: '1px solid #ccc',
                    borderRadius: '10px',
                    padding: '1rem',
                    marginBottom: '1.5rem',
                    height: '200px',
                    overflowY: 'auto',
                    display: 'flex',
                    alignItems: 'center',
                    justifyContent: 'center',
                }}
            >
                <Typography variant="body1" color="textSecondary">
                    Chat functionality is disabled for now.
                </Typography>
            </Box>
        </Container>
    );
};

export default MainContent;
