import React, { useState } from 'react';
import { Box, Button, keyframes } from '@mui/material';
import CenteredDiv from './CenteredDiv';

const LayoutMode = () => {
    const [currentLayout, setCurrentLayout] = useState('default');

    // Define different layout styles
    const layouts = {
        default: {
            display: 'flex',
            justifyContent: 'center',
            alignItems: 'center',
            height: '300px',
        },
        grid: {
            display: 'grid',
            gridTemplateColumns: 'repeat(3, 1fr)',
            gridGap: '10px',
            height: '300px',
        },
        chaotic: {
            position: 'relative',
            height: '300px',
            overflow: 'hidden', // Prevent elements from escaping
        },
    };

    const generateBouncingKeyframes = (containerWidth, containerHeight) => {
        const maxX = containerWidth - 50; // Max horizontal movement (subtract element size)
        const maxY = containerHeight - 50; // Max vertical movement (subtract element size)

        return keyframes`
            0% { transform: translate(0, 0); }
            25% { transform: translate(${maxX}px, 0); }
            50% { transform: translate(${maxX}px, ${maxY}px); }
            75% { transform: translate(0, ${maxY}px); }
            100% { transform: translate(0, 0); }
        `;
    };

    const generateChaos = (containerRef) => {
        const containerWidth = containerRef.current.offsetWidth;
        const containerHeight = containerRef.current.offsetHeight;

        const chaosDivs = Array.from({ length: 5 }, (_, i) => {
            const bouncingKeyframes = generateBouncingKeyframes(containerWidth, containerHeight);
            const randomDuration = `${Math.random() * 3 + 2}s`; // Random duration between 2s and 5s

            return (
                <Box
                    key={i}
                    sx={{
                        position: 'absolute',
                        top: `${Math.random() * (containerHeight - 50)}px`,
                        left: `${Math.random() * (containerWidth - 50)}px`,
                        width: '50px',
                        height: '50px',
                        backgroundColor: 'rgba(255,0,0,0.5)',
                        animation: `${bouncingKeyframes} ${randomDuration} linear infinite`,
                    }}
                />
            );
        });
        return chaosDivs;
    };

    const containerRef = React.useRef();

    return (
        <Box>
            <Box sx={{ display: 'flex', justifyContent: 'center', gap: '1rem', marginBottom: '1rem' }}>
                <Button variant="outlined" onClick={() => setCurrentLayout('default')}>
                    Default Layout
                </Button>
                <Button variant="outlined" onClick={() => setCurrentLayout('grid')}>
                    Grid Layout
                </Button>
                <Button variant="outlined" onClick={() => setCurrentLayout('chaotic')}>
                    Chaotic Layout
                </Button>
            </Box>
            <Box
                ref={containerRef}
                sx={{ border: '1px solid #ccc', borderRadius: '8px', ...layouts[currentLayout] }}
            >
                {currentLayout === 'chaotic' && generateChaos(containerRef)}
                <CenteredDiv />
            </Box>
        </Box>
    );
};

export default LayoutMode;
