import React, { useState, useEffect } from 'react';
import axios from 'axios';

const MainContent = () => {
    // State for counter
    const [count, setCount] = useState(0);

    // State for random joke
    const [joke, setJoke] = useState('');
    const [loading, setLoading] = useState(true);

    // State for user input form
    const [input, setInput] = useState('');
    const [submittedText, setSubmittedText] = useState('');

    // State for backend response
    const [responseMessage, setResponseMessage] = useState('');

    // Fetch a random joke on component mount
    useEffect(() => {
        axios
            .get('https://official-joke-api.appspot.com/random_joke')
            .then((response) => {
                setJoke(`${response.data.setup} ${response.data.punchline}`);
                setLoading(false);
            })
            .catch((error) => {
                console.error('Error fetching joke:', error);
                setJoke('Failed to load joke.');
                setLoading(false);
            });
    }, []);

    // Handle form submission and send data to backend
    const handleSubmit = async (e) => {
        e.preventDefault();
        setSubmittedText(input);

        try {
            const response = await axios.post('http://localhost:5000/data', { input });
            setResponseMessage(response.data.message);
        } catch (error) {
            console.error('Error sending data to backend:', error);
            setResponseMessage('Error: Could not send data to the backend.');
        }

        setInput('');
    };

    return (
        <main style={{ padding: '2rem', textAlign: 'center' }}>
            {/* Centered Div */}
            <div style={{ width: '200px', height: '200px', margin: 'auto', backgroundColor: '#61dafb', marginBottom: '2rem' }}>
                Centered Div
            </div>

            {/* Counter */}
            <div style={{ marginBottom: '2rem' }}>
                <p style={{ fontSize: '1.5rem', margin: '0.5rem' }}>Count: {count}</p>
                <button onClick={() => setCount(count + 1)} style={{ padding: '0.5rem 1rem', fontSize: '1rem' }}>
                    Increment
                </button>
            </div>

            {/* Joke Display */}
            <div style={{ marginBottom: '2rem', padding: '1rem', backgroundColor: '#f0f0f0', borderRadius: '10px' }}>
                <h3>Random Joke</h3>
                <p>{loading ? 'Loading...' : joke}</p>
            </div>

            {/* User Input Form */}
            <div style={{ padding: '1rem', backgroundColor: '#f7f7f7', borderRadius: '10px', marginBottom: '2rem' }}>
                <form onSubmit={handleSubmit}>
                    <input
                        type="text"
                        value={input}
                        onChange={(e) => setInput(e.target.value)}
                        placeholder="Type something..."
                        style={{ padding: '0.5rem', width: '80%', marginBottom: '1rem' }}
                    />
                    <button type="submit" style={{ padding: '0.5rem 1rem' }}>
                        Submit
                    </button>
                </form>
                {submittedText && <p style={{ marginTop: '1rem' }}>Submitted: {submittedText}</p>}
                {responseMessage && <p style={{ marginTop: '1rem', color: 'green' }}>{responseMessage}</p>}
            </div>
        </main>
    );
};

export default MainContent;
